import com.juddsolutions.groovy.basics.Student
def student = new Student(firstName: "Chris", lastName: "Judd")println "Student class: ${student.class}"// dumpprintln "\nDump:"println student.dump()// list propertiesprintln "\nProperties:"student.properties.each{println it}// list fieldsprintln "\nDeclared Field: "student.class.declaredFields.each{println it}// list methodsprintln "\nMethods:"student.class.methods.each{println it} //list constructorsprintln "\nConstructors:"Student.constructors.each{println it} //list interfacesprintln "\nInterfaces:"Student.interfaces.each{println it}// to prevent the dreaded MissingPropertyExceptionif(student.metaClass.hasProperty(student, "firstName")){ 	student.firstName = "Jim" 	// or 	// student.setProperty("firstName", "Jim")} println student.dump()// calling methods with a string nameprintln student."firstName"// to prevent the dreaded MissingMethodExceptionif(student.metaClass.respondsTo(student, "getFirstName")){ 	println student.getFirstName() } 